mapper.select:
SELECT id, user_id, content_id, type, create_time, content_type
FROM active_net


        AND user_id = #{userId}



        AND content_id = #{contentId}



        AND content_type = #{contentType}



        AND type = #{type}





    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM active_net


        AND user_id = #{userId}



        AND content_id = #{contentId}



        AND content_type = #{contentType}



        AND type = #{type}

SELECT an.content_id,
COUNT(an.id) AS score
FROM active_net an
WHERE an.content_type = #{contentType}
AND an.type = #{type}
GROUP BY content_id
ORDER BY score desc
LIMIT 0, #{count}
